import React from 'react';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';
import styled from 'styled-components';
import { faTrash} from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export default class App extends React.Component{
  render(){
    return (<React.Fragment>
      <Container fluid>
          <Row style={{"min-height": "100vh"}}>
            <Col lg="8" className="p-0">
              <ColOutWrapper className="d-flex justify-content-center">
                 <ColInnerWrapper>
                  <h4 className="text-center">CRUD APPLICATION</h4>
                  <AddWrapper>
                    <input type="text" placeholder="Enter product name" />
                    <input type="text" placeholder="Enter price" />
                    <input type="submit" value="Add" />
                  </AddWrapper>
                  <div>
                    <WrappedList>
                      <span>Name</span>
                      <p contentEditable="true">13324</p>
                      <span><FontAwesomeIcon icon={faTrash} color="#e63946"/></span>
                    </WrappedList>
                  </div>
                 </ColInnerWrapper>
              </ColOutWrapper>
            </Col>
            <Col lg="4" className="p-0">
              <APIStory>Hello </APIStory>
            </Col>
          </Row>
      </Container>
    </React.Fragment>);
  }
}

const APIStory = styled.div`
   padding: 0px 15px; 
   padding-top: 30px;
   height:  100%;
   background: #f1faee;
   backgrond-color: #f1faee;
`;


const WrappedList = styled.div`
  // background: #0566d;
  background: #264653;
  padding: 8px 10px;
  font-size: 1rem;
  border-radius: 4px;
  display: flex;
  > *{
    flex: 2 0 2;
  }
  > span:nth-of-type(1){
    font-size: 1.13rem;
    margin: auto;
    margin-left: 10px;
  }
  > span:nth-of-type(1){
    border-right: 2px solid #fff;
  }
  >span:nth-of-type(2){
   flex: 0; 
  }
  > p{ 
    flex: auto;
    margin: auto 5px;
    margin-left: 10px; 
    border:none;
    outline: none;
    background: inherit;
    background-color: inherit;
    color: inherit;
  }
`;

const AddWrapper = styled.div`
  margin: 30px auto;
  padding: 10px;
  border: none;
  border-radius: 4px;
  font-size: 1.05rem;
  color: #fff;
  box-shadow: 1px 1px 44px -12px rgba(255,255,255,0.6);
  background: #ef476f;
  > input {
    border: none;
    background: inherit;
    outline: none;
    color: #fff;
    padding-left: 10px;
    margin: auto 5px;
  }
  > input::placeholder{
    color: #fff;
  }
  > input:nth-child(1){
    border-right: 2px solid #fdfffc;
  }
  > input[type="submit"]{
    min-width: 80px;
    border: none; 
    outline: none;
    background: #fca311;
    padding: 3px 0px;
    background-color: #fca311;
    border-radius: 4px;
    box-shadow: 1px 1px 0px 1px rgba(0,0,0,0.05);
    color:#fff;
  }
  >  input[type="submit"]:active{
      box-shadow: 2px 2px 0px 1px rgba(0,0,0,0.05),-2px -2px 0px 1px rgba(0,0,0,0.05);
      background: #ff9f1c;
  }
`;

const ColOutWrapper = styled.div`
  background: #2ec4b6;
  background-color: #2ec4b6;
  color: #fff;
  font-size: 1.3  rem;
  font-weight: 500;
  height: 100%;
  // padding-top: 20px;
`;
const ColInnerWrapper = styled.div`
  margin: 10px;
  margin-top: 30px;
`;
