import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import { Container, Row, Col, Alert } from 'react-bootstrap';
import styled from 'styled-components';
import { faTrash, faCheck} from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export default class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {listData: [],
                  tempListData: [],
                  isListLoading: true,
                  errorMessage: "",
                  message: "",
                  messageClass: "",
                };
  }
  async componentDidMount(){
    try{
      const url = "https://fidisys-api.herokuapp.com/items";
      const response = await fetch(url);
      let data = await response.json();
      this.setState({listData: data},()=>{
        this.setState({isListLoading: false});
      });
    }catch(error){
      this.setState({errorMessage: "Unable to fetch data",isListLoading: false});
    }  

  }
  parFocus(val){
    // console.log(val.target.parentNode.getAttribute("data-id"));
  }
  async iconTouchClick(e){  
    let id = e.target.parentNode.parentNode.parentNode.getAttribute("data-id");
    console.log(id);
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id })
    };
    try{
      const response = await fetch('https://fidisys-api.herokuapp.com/delete', requestOptions)
      let data = await response.json();
      if(data['status'] !== "deleted"){
        this.setState({message: "Successfully deleted the item",messageClass: "success"});
      }else if(data['error']){
        this.setState({message: data['error'],messageClass: "dark"});
      }
    }catch(error){
      console.log(error);
      this.setState({message: "Can't detete items",messageClass: "dark"});
    }
  }
  render(){
    console.log(this.state.listData);
    let wrapperList = this.state.listData.map((val)=>{
      return (<React.Fragment>
          <WrappedList key={val.id} data-id={val.id}>
            <span>{val.name}</span>
            <p contentEditable="true"
            onFocus={this.parFocus.bind(this)}>{val.price}</p>
            <span className="btn" onTouch={this.iconTouchClick.bind(this)}
             onClick={this.iconTouchClick.bind(this)}>
              <FontAwesomeIcon  icon={faTrash} color="#fcbf49"/></span>
          </WrappedList>
      </React.Fragment>);
    });

    return (<React.Fragment>
      <Container fluid>
          <Row style={{"min-height": "100vh"}}>
            <Col lg="8" className="p-0">
              <ColOutWrapper className="d-flex justify-content-center">
                 <ColInnerWrapper>
                  <h4 className="text-center">CRUD APPLICATION</h4>
                  <AddWrapper>
                    <input type="text" placeholder="Enter product name" />
                    <input type="text" placeholder="Enter price" />
                    <input type="submit" value="Add" />
                  </AddWrapper>
                  <div>
        {this.state.message !== "" ? <Alert variant={this.state.messageClass}>{this.state.message}</Alert> : ""}
                    {this.state.errorMessage !== "" ? <h4 className="text-center">{this.state.errorMessage}</h4>: ""}
                    {this.state.isListLoading ? <h4 className="text-center">Loading....</h4> : wrapperList}
                  </div>
                 </ColInnerWrapper>
              </ColOutWrapper>
            </Col>
            <Col lg="4" className="p-0">
              <APIStory>
                <h4>API ENDPOINTS:</h4>
                <Command>"/items"  --  to get data of all items</Command>
                <Command>"/add"    --  to add item with name and price</Command>
                <Command>"/update" --  to edit price of exiting item with id</Command>
                <Command>"/delete" -- to delete item with id</Command>
              </APIStory>
            </Col>
          </Row>
      </Container>
    </React.Fragment>);
  }
}

const Command =  styled.div`
  padding: 5px 10px;
  border-radius: 6px;
  margin: 20px auto;
  font-size: 1.05rem;
  border: 1px solid #e5e5e5;
  background: #edf2f4;
  background-color: #edf2f4;
  color: rgba(0,0,0,0.8);
  :nth-of-type(1){
    margin-top: 5px;
  }
`;

const APIStory = styled.div`
   padding: 0px 15px; 
   padding-top: 30px;
   height:  100%;
   background: #f1faee;
   backgrond-color: #f1faee;
`;




const WrappedList = styled.div`
  // background: #0566d;
  background: #264653;
  margin: 10px auto;
  padding: 8px 10px;
  font-size: 1rem;
  border-radius: 4px;
  display: flex;
  > *{
    flex: 1;
    flex-shrink: 0;
  }
  > span:nth-of-type(1){
    font-size: 1.13rem;
    margin: auto;
    margin-left: 10px;
  }
  > span:nth-of-type(1){
    border-right: 2px solid #fff;
  }
  >span:nth-of-type(2){
   flex: 0; 
  }
  >span:nth-of-type(2):hover{
    cursor: pointer;
  }
  >span:nth-of-type(2):active > svg{
    color: #e63946;
  }
  > p{ 
    flex: auto;
    margin: auto 5px;
    margin-left: 10px; 
    border:none;
    outline: none;
    background: inherit;
    background-color: inherit;
    color: inherit;
  }
`;

const AddWrapper = styled.div`
  margin: 30px auto;
  padding: 10px;
  border: none;
  border-radius: 4px;
  font-size: 1.05rem;
  color: #fff;
  box-shadow: 1px 1px 44px -12px rgba(255,255,255,0.6);
  background: #ef476f;
  > input {
    border: none;
    background: inherit;
    outline: none;
    color: #fff;
    padding-left: 10px;
    margin: auto 5px;
  }
  > input::placeholder{
    color: #fff;
  }
  > input:nth-child(1){
    border-right: 2px solid #fdfffc;
  }
  > input[type="submit"]{
    min-width: 80px;
    border: none; 
    outline: none;
    background: #fca311;
    padding: 3px 0px;
    background-color: #fca311;
    border-radius: 4px;
    box-shadow: 1px 1px 0px 1px rgba(0,0,0,0.05);
    color:#fff;
  }
  >  input[type="submit"]:active{
      box-shadow: 2px 2px 0px 1px rgba(0,0,0,0.05),-2px -2px 0px 1px rgba(0,0,0,0.05);
      background: #ff9f1c;
  }
`;

const ColOutWrapper = styled.div`
  background: #2ec4b6;
  background-color: #2ec4b6;
  color: #fff;
  font-size: 1.3  rem;
  font-weight: 500;
  height: 100%;
  // padding-top: 20px;
`;
const ColInnerWrapper = styled.div`
  margin: 10px;
  margin-top: 30px;
`;
