  background: #264653;
  margin: 10px auto;
  padding: 8px 10px;
  font-size: 1rem;
  border-radius: 4px;
  display: flex;
  > *{
    flex: 1;
    flex-shrink: 0;
  }
  > span:nth-of-type(1){
    font-size: 1.13rem;
    margin: auto;
    margin-left: 10px;
  }
  > span:nth-of-type(1){
    border-right: 2px solid #fff;
  }
  >span:nth-of-type(2){
   flex: 0; 
  }
  >span:nth-of-type(2):hover{
    cursor: pointer;
  }
  >span:nth-of-type(2):active > svg{
    color: #e63946;
  }
  > p{ 
    flex: auto;
    margin: auto 5px;
    margin-left: 10px; 
    border:none;
